import { createGlobalStyle } from 'styled-components';
import normalize from 'normalize.css';

export default createGlobalStyle`
  ${normalize}

  *, *:before, *:after {
    box-sizing: border-box;
  }

  body,
  html {
    height: 100%;
    margin: 0;
  }

  body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
      Oxygen-Sans, Ubuntu, Cantarell, 'Helvetica Neue', sans-serif;
    background-color: #fff;
    line-height: 1.4;
  }

  a {
    display: inline-block; /* Makes the link behave like a button */
    padding: 0.5em 1em; /* Add spacing inside the button */
    font-size: 1em; /* Adjust font size */
    font-weight: bold;
    color: #000; /* Black text color */
    text-decoration: none; /* Remove underline */
    background-color: transparent; /* Transparent background */
    border-radius: 5px; /* Optional: Rounded corners */
    transition: background-color 0.3s ease, color 0.3s ease; /* Smooth transition for hover/focus effects */
  }

  a:visited {
    color: #000; /* Same color as unvisited links */
  }

  a:hover {
    background-color: rgba(0, 0, 0, 0.2); /* 20% opaque black background on hover */
  }

  a:focus {
    background-color: rgba(0, 0, 0, 0.5); /* 50% opaque black background on focus */
    color: #fff; /* White text color on focus */
    outline: none; /* Remove default focus outline */
  }

  code,
  pre {
    max-width: 100%;
    overflow: auto;
    margin: 0 auto;
  }
`;
