import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { useQuery } from '@apollo/client';
import { GET_ME } from '../gql/query'; // Make sure this query returns the current user's details

const Nav = styled.nav`
  padding: 1em;
  background: #f5f4f0;

  @media (max-width: 700px) {
    padding-top: 64px;
  }

  @media (min-width: 700px) {
    position: fixed;
    width: 220px;
    height: calc(100% - 64px);
    overflow-y: scroll;
  }
`;

const NavList = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
  line-height: 2;

  /* We can nest styles in styled-components */
  /* The following styles will apply to links within the NavList component */
  a {
    text-decoration: none;
    font-weight: bold;
    font-size: 1.1em;
    color: #333;
  }

  a:visited {
    color: #333;
  }

  a:hover,
  a:focus {
    color: #0077cc;
  }
`;

const Navigation = () => {
  // Fetch the current user data using GET_ME query
  const { loading, error, data } = useQuery(GET_ME);

  if (loading) return null; // You can add a loading state here
  if (error) return <p>Error loading user data!</p>;

  return (
    <Nav>
      <NavList>
        <li>
          <Link to="/">
            <span aria-hidden="true" role="img">
              üè†
            </span>
            Home
          </Link>
        </li>
        <li>
          <Link to="/mynotes">
            <span aria-hidden="true" role="img">
              üìì
            </span>
            My Notes
          </Link>
        </li>
        <li>
          <Link to="/favorites">
            <span aria-hidden="true" role="img">
              üåü
            </span>
            Favorites
          </Link>
        </li>
        <li>
          <Link to="/new">
            <span aria-hidden="true" role="img">
              ‚ûï
            </span>
            New
          </Link>
        </li>

        {/* Conditionally render the "Users" link if the user is admin */}
        {data.me.username === 'admin' && (
          <li>
            <Link to="/users">
              <span aria-hidden="true" role="img">
                üë•
              </span>
              Users
            </Link>
          </li>
        )}
      </NavList>
    </Nav>
  );
};

export default Navigation;
