import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

// Styled components
const Nav = styled.nav`
  position: fixed;
  top: 0;
  left: ${({ isCollapsed }) => (isCollapsed ? '-90px' : '0')};
  width: 180px;
  height: 100%;
  background: #f5f4f0;
  z-index: 1000;
  transition: left 0.3s ease;
`;

const MenuButton = styled.button`
  position: absolute;
  top: 1em;
  right: -40px;
  width: 40px;
  height: 40px;
  background: #333;
  color: #fff;
  border: none;
  border-radius: 50%;
  cursor: pointer;
  z-index: 1100;

  &:hover {
    background: #555;
  }
`;

const NavList = styled.ul`
  list-style: none;
  padding: 1em;
  margin: 0;

  li {
    margin: 1em 0;
  }

  a {
    text-decoration: none;
    color: #333;
    font-weight: bold;
    display: block;
    padding: 0.5em;
    border-radius: 8px;
    transition: background 0.3s ease;

    &:hover {
      background: rgba(0, 0, 0, 0.1);
    }
  }
`;

const Navigation = () => {
  const [isCollapsed, setIsCollapsed] = useState(true);

  return (
    <Nav isCollapsed={isCollapsed}>
      <MenuButton onClick={() => setIsCollapsed(!isCollapsed)}>
        {isCollapsed ? '‚ò∞' : '√ó'}
      </MenuButton>
      <NavList>
        <li>
          <Link to="/">üè† Home</Link>
        </li>
        <li>
          <Link to="/mytasks">üìì My Tasks</Link>
        </li>
        <li>
          <Link to="/favorites">üåü Favorites</Link>
        </li>
        <li>
          <Link to="/new">‚ûï New</Link>
        </li>
        <li>
          <Link to="/profile">üë§ Profile</Link>
        </li>
        <li>
          <Link to="/users">üë• Users</Link>
        </li>
      </NavList>
    </Nav>
  );
};

export default Navigation;
