import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { useQuery } from '@apollo/client';
import { gql } from '@apollo/client';
import { GET_ME } from '../gql/query'; // Query to fetch the current user's details

// Local cache query to check if the user is logged in
const IS_LOGGED_IN = gql`
  {
    isLoggedIn @client
  }
`;

const Nav = styled.nav`
  padding: 1em;
  background: #f5f4f0;

  @media (max-width: 700px) {
    padding-top: 64px;
  }

  @media (min-width: 700px) {
    position: fixed;
    width: 250px;
    height: calc(100% - 64px);
    overflow-y: scroll;
  }
`;

const NavList = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
  line-height: 2;
  li {
    width: 100%;  // Make each li element take the same width
  }

  a {
    display: block;  // Make the link take the full width of the li
    text-decoration: none;
    font-weight: bold;
    font-size: 1.1em;
    color: #333;
    padding: 1em;  // Add some padding for spacing
    text-align: center;  // Optional: Center the text inside the link
    width: 100%;  // Ensure the link takes the full width of the li
  }

  a:visited {
    color: #333;
  }

  a:hover,
  a:focus {
    color: #0077cc;
  }
`;

const Navigation = () => {
  // Check if the user is logged in using the IS_LOGGED_IN query
  const { data: loggedInData } = useQuery(IS_LOGGED_IN);

  // Execute the GET_ME query only if the user is logged in
  const { loading, error, data } = useQuery(GET_ME, {
    skip: !loggedInData?.isLoggedIn, // Skip the query if the user is not logged in
  });

  if (loading) return null; // You can add a loading spinner or state here
  if (error && loggedInData?.isLoggedIn) {
    // Handle error only if the user is supposed to be logged in
    console.error(error);
    return <p>Error loading user data. Please try again later.</p>;
  }

  const isLoggedIn = loggedInData?.isLoggedIn;
  const isAdmin = data?.me?.username === 'admin';

  return (
    <Nav>
      <NavList>
        <li>
          <Link to="/">
            <span aria-hidden="true" role="img">
              🏠
            </span>
            Home
          </Link>
        </li>
        <li>
          <Link to="/mynotes">
            <span aria-hidden="true" role="img">
              📓
            </span>
            My Tasks
          </Link>
        </li>
        <li>
          <Link to="/favorites">
            <span aria-hidden="true" role="img">
              🌟
            </span>
            Favorites
          </Link>
        </li>
        <li>
          <Link to="/new">
            <span aria-hidden="true" role="img">
              ➕
            </span>
            New
          </Link>
        </li>
        {isLoggedIn && (
          <li>
            <Link to="/profile">
              <span aria-hidden="true" role="img">
                👤
              </span>
              Profile
            </Link>
          </li>
        )}
        {/* Conditionally render the "Users" link if the user is logged in and is admin */}
        {isLoggedIn && isAdmin && (
          <li>
            <Link to="/users">
              <span aria-hidden="true" role="img">
                👥
              </span>
              Users
            </Link>
          </li>
        )}
      </NavList>
    </Nav>
  );
};

export default Navigation;
